let allJobSitemaps = [];
let seenUrls = new Set();

// Change this to control how many links you want
const maxLinks = $('main_config').first().json.search_config.maxLinkExtractor;

for (let item of items) {
    let sitemaps = item.json.sitemapindex.sitemap;

    // Ensure sitemaps is always an array
    if (!Array.isArray(sitemaps)) {
        sitemaps = [sitemaps];
    }

    for (let s of sitemaps) {
        if (s.loc && s.loc.includes('job_ads_sitemap') && !seenUrls.has(s.loc)) {
            allJobSitemaps.push({ json: { url: s.loc } });
            seenUrls.add(s.loc);

            if (allJobSitemaps.length >= maxLinks) {
                return allJobSitemaps; // âœ… now this exits the Function node itself
            }
        }
    }
}

return allJobSitemaps;

// This node will parse a large XML sitemap string more safely.
// Instead of parsing the whole XML into a giant JSON, it extracts <loc> tags directly.

const xmlData = items[0].json.data; // The big 15MB XML string
const regex = /<loc>(.*?)<\/loc>/g; // Capture everything inside <loc> ... </loc>

let matches;
let results = [];
let counter = 0;
const maxLinks = 500; // <-- adjust this to limit how many links to pull per batch

while ((matches = regex.exec(xmlData)) !== null) {
  results.push({ json: { link: matches[1] } });
  counter++;

  // Break early if you want to process in smaller chunks
  if (counter >= maxLinks) break;
}

return results;

// Get config from the first input item
const cfg = items[0].json;

// New structure: search_keywords and search_config at root
const keywords = cfg.search_config?.search_keywords || cfg.keywords || [];
const searchConfig = cfg.search_config || {};

// Fallback values
const maxPages = searchConfig.maxPagesPerQuery || 1;
const maxLinks = searchConfig.maxLinkExtractor || 1;
const batchSizeSitemap = searchConfig.batchSizeSitemapLinkLoop || 1;
const batchSizeJob = searchConfig.batchSizeJobLinkLoop || 1;
const pageLanguage = searchConfig.pageLanguage || '';

const out = [];

for (const site of cfg.sites) {
    for (const keyword of keywords) {
        const baseItem = { 
            ...site,
            keyword,
            search_config: {
                maxPagesPerQuery: maxPages,
                maxLinkExtractor: maxLinks,
                batchSizeSitemapLinkLoop: batchSizeSitemap,
                batchSizeJobLinkLoop: batchSizeJob,
                pageLanguage
            }
        };

        if (!site.allowedToScrape) {
            // Only push once per site if scraping is forbidden
            if (keywords.indexOf(keyword) === 0) {
                out.push({ json: { ...baseItem, skipped: true } });
            }
            continue;
        }

        // Expand URLs for allowed-to-scrape sites
        for (let p = 1; p <= maxPages; p++) {
            let url = site.searchTemplate
                ? site.searchTemplate
                    .replace('{{keyword}}', encodeURIComponent(keyword))
                    .replace('{{page}}', p)
                    .replace('{{pageOffset}}', (p - 1) * 10)
                : site.baseUrl;

            out.push({
                json: {
                    ...baseItem,
                    url,
                    page: p,
                    useRenderer: !!site.useRenderer
                }
            });
        }
    }
}

return out;

