// Get keywords from the earlier node (replace 'main_config' with your node name)
const keywords = $node["main_config"].json.keywords;

const output = [];

for (const site of websites) {
  for (const kw of keywords) {
    // Use searchTemplate if it exists, otherwise fallback to baseUrl
    const searchUrl = site.searchTemplate 
      ? site.searchTemplate.replace('{{keyword}}', encodeURIComponent(kw)) 
      : site.baseUrl;

    output.push({
      json: {
        websiteName: site.name,
        baseUrl: site.baseUrl,
        keyword: kw,
        searchUrl: searchUrl
      }
    });
  }
}

return output;

// Configuration flags
const filterByKeyword = true;

// Get all keywords from main_config
const mainConfig = $('main_config').first().json;
const keywords = mainConfig.search_config.search_keywords || [];

// Input items from previous node
const items = $input.all();
const filtered = [];

for (const item of items) {
    const link = item.json.link.toLowerCase();

    // Keyword filtering
    const matchesKeyword = filterByKeyword 
        ? keywords.some(kw => link.includes(kw.replace(/\s+/g, '-').toLowerCase()))
        : true;

    if (matchesKeyword) {
        filtered.push({ json: { link: item.json.link } });
    }
}

return filtered;

const langFilter = $('main_config').first().json.search_config.pageLanguage;

// Input items from previous node
const items = $input.all();
const filtered = [];

for (const item of items) {
    const link = item.json.link.toLowerCase();

    // Keep only URLs that contain 
    if (link.includes(langFilter)) {
        filtered.push({ json: { link: item.json.link } });
    }
}

return filtered;

const filters = $('main_config').first().json.search_config.linkEtiquetteFilter; 
const results = [];

for (const item of items) {
  const response = item.json; // Google API response

  if (response.items) {
    for (const r of response.items) {
      const isJobLike = filters.some(f => r.link.includes(f));

      if (isJobLike) {
        results.push({
          json: {
            website: item.json.websiteName || "unknown",
            keyword: item.json.keyword || "unknown",
            title: r.title,
            url: r.link,
            snippet: r.snippet,
          }
        });
      }
    }
  }
}

return results;
