let allJobSitemaps = [];
let seenUrls = new Set();

// Change this to control how many links you want
const maxLinks = $('main_config1').first().json.search_config.maxLinkExtractor;

for (let item of items) {
    let sitemaps;

    if (item.json.sitemapindex && item.json.sitemapindex.sitemap) {
        sitemaps = item.json.sitemapindex.sitemap;
    } else if (item.json.urlset && item.json.urlset.url) {
        sitemaps = item.json.urlset.url;
    } else {
        continue;
    }

    if (!Array.isArray(sitemaps)) sitemaps = [sitemaps];

    for (let s of sitemaps) {
        const loc = s.loc;
        if (!loc) continue;

        // Filter URLs by sitemapPattern or default to 'job'
        const pattern = "/job";
        if (!loc.includes(pattern)) continue;

        if (!seenUrls.has(loc)) {
            allJobSitemaps.push({ json: { url: loc } });
            seenUrls.add(loc);

            if (allJobSitemaps.length >= maxLinks) {
                return allJobSitemaps;
            }
        }
    }
}

return allJobSitemaps;

// This node parses both XML sitemaps and normal HTML pages safely.
// If <loc> tags are found, it extracts them (XML sitemap mode).
// Otherwise, it falls back to extracting <a href="..."> links (HTML mode).

const xmlData = items[0].json.data || '';
const results = [];
const maxLinks = 500; // Limit to prevent memory overload

let matches;
let counter = 0;

// Regex patterns
const regexLoc = /<loc>(.*?)<\/loc>/gi; // XML sitemap <loc>...</loc>
const regexHref = /<a\s+(?:[^>]*?\s+)?href="([^"]*)"/gi; // HTML <a href="...">

// Try to detect whether this looks like a sitemap
const looksLikeSitemap =
  xmlData.includes('<urlset') ||
  xmlData.includes('<sitemapindex') ||
  xmlData.match(regexLoc);

// Use appropriate regex depending on content
const activeRegex = looksLikeSitemap ? regexLoc : regexHref;

while ((matches = activeRegex.exec(xmlData)) !== null) {
  // Extract link (group 1 in regex)
  const link = matches[1].trim();

  // Skip empty or junk links
  if (!link || link.startsWith('#') || link.startsWith('javascript:')) continue;

  results.push({ json: { link } });
  counter++;

  if (counter >= maxLinks) break; // stop early if too many
}

return results.length
  ? results
  : [{ json: { message: 'No links found in provided data.' } }];

// Get config from the first input item
const cfg = items[0].json;

// New structure: search_keywords and search_config at root
const keywords = cfg.search_config?.search_keywords || cfg.keywords || [];
const searchConfig = cfg.search_config || {};

// Fallback values
const maxPages = searchConfig.maxPagesPerQuery || 1;
const maxLinks = searchConfig.maxLinkExtractor || 1;
const batchSizeSitemap = searchConfig.batchSizeSitemapLinkLoop || 1;
const batchSizeJob = searchConfig.batchSizeJobLinkLoop || 1;
const pageLanguage = searchConfig.pageLanguage || '';

const out = [];

for (const site of cfg.sites) {
    for (const keyword of keywords) {
        const baseItem = { 
            ...site,
            keyword,
            search_config: {
                maxPagesPerQuery: maxPages,
                maxLinkExtractor: maxLinks,
                batchSizeSitemapLinkLoop: batchSizeSitemap,
                batchSizeJobLinkLoop: batchSizeJob,
                pageLanguage
            }
        };

        if (!site.allowedToScrape) {
            // Only push once per site if scraping is forbidden
            if (keywords.indexOf(keyword) === 0) {
                out.push({ json: { ...baseItem, skipped: true } });
            }
            continue;
        }

        // Expand URLs for allowed-to-scrape sites
        for (let p = 1; p <= maxPages; p++) {
            let url = site.searchTemplate
                ? site.searchTemplate
                    .replace('{{keyword}}', encodeURIComponent(keyword))
                    .replace('{{page}}', p)
                    .replace('{{pageOffset}}', (p - 1) * 10)
                : site.baseUrl;

            out.push({
                json: {
                    ...baseItem,
                    url,
                    page: p,
                    useRenderer: !!site.useRenderer
                }
            });
        }
    }
}

return out;

// Configuration: set to true to process only the first website, false for all
const processFirstOnly = true;

// Extract websites and keywords from your n8n items and config
const websites = items.map(item => item.json);
const keywords = $('main_config1').first().json.search_config.search_keywords;

const output = [];
const targetWebsites = processFirstOnly ? [websites[0]] : websites;

for (const site of targetWebsites) {
  if (!site) continue; // skip empty entries

  for (const kw of keywords) {
    const baseDomain = site.baseUrl.replace(/^https?:\/\//, '');
    const searchPath = site.searchPathHint || '';  // fallback if not defined

    // Build the site-specific query
    const query = `${kw} site:${baseDomain}${searchPath}`;

    // Encode query for safe use in URLs / API requests
    const encodedQuery = encodeURIComponent(query);

    // Push the result
    output.push({
      json: {
        websiteName: site.name,
        baseUrl: site.baseUrl,
        keyword: kw,
        query: query,
        encodedQuery: encodedQuery  // optional field for direct use in requests
      }
    });
  }
}

return output;

// Configuration flags
const filterByKeyword = true;

// Get all keywords from main_config
const mainConfig = $('main_config1').first().json;
const keywords = mainConfig.search_config.search_keywords || [];

// Input items from previous node
const items = $input.all();
const filtered = [];

for (const item of items) {
    const link = item.json.link.toLowerCase();

    // Keyword filtering
    const matchesKeyword = filterByKeyword 
        ? keywords.some(kw => link.includes(kw.replace(/\s+/g, '-').toLowerCase()))
        : true;

    if (matchesKeyword) {
        filtered.push({ json: { link: item.json.link } });
    }
}

return filtered;

const langFilter = $('main_config1').first().json.search_config.pageLanguage;

// Input items from previous node
const items = $input.all();
const filtered = [];

for (const item of items) {
    const link = item.json.link.toLowerCase();

    // Keep only URLs that contain 
    if (link.includes(langFilter)) {
        filtered.push({ json: { link: item.json.link } });
    }
}

return filtered;

const filters = $('main_config1').first().json.search_config.linkEtiquetteFilter;
const results = [];

for (const item of items) {
  const response = item.json; // Google API response

  if (response.items) {
    for (const r of response.items) {
      const textToSearch = [
        r.link || "",
        r.title || "",
        r.snippet || ""
      ].join(" ").toLowerCase();

      const matchedFilter = filters.find(f => textToSearch.includes(f.toLowerCase()));

      if (matchedFilter) {
        results.push({
          json: {
            website: item.json.websiteName || "unknown",
            keyword: item.json.keyword || "unknown",
            title: r.title,
            url: r.link,
            snippet: r.snippet,
            matchedFilter: matchedFilter
          }
        });
      }
    }
  }
}

return results;

const baseURL = $('main_config1').first().json.sites[1].baseUrl;
let results = [];

items.forEach(item => {
  // Ensure jobLink is an array
  let links = item.json.jobLink;
  if (!Array.isArray(links)) links = [links];

  // Ensure jobTitle is an array (repeat first title if only one)
  let titles = item.json.jobTitle;
  if (!Array.isArray(titles)) titles = [titles];

  links.forEach((link, index) => {
    link = (link || '').trim();
    const absoluteLink = link.startsWith('http') ? link : baseURL + link;

    // Pick corresponding title if exists, else empty
    const job_title = (titles[index] || titles[0] || '').trim();

    if (absoluteLink) results.push({ json: { job_title, link: absoluteLink } });
  });
});

// Deduplicate by link
const uniqueResults = [...new Map(results.map(r => [r.json.link, r.json])).values()];

return uniqueResults.map(r => ({ json: r }));

// Process all input items
return items.map(item => {
    const url = item.json.link;  // Original link

    // Extract the part after the UUID
    const match = url.match(/\/job-offers\/[0-9a-fA-F-]+-(.+)/);
    if (!match) {
        return { json: { link: url, company: null, job_title: null } };
    }

    const remaining = match[1];
    const parts = remaining.split('-');

    // Heuristic to get company name (first 1-3 words)
    let company = parts[0];
    let i;
    for (i = 1; i < Math.min(3, parts.length); i++) {
        if (!['stage', 'intern', 'junior', 'senior', 'developer', 'manager', 'global', 'traineeship', 'trainee', 'graduate', 'internship', '', 'engineer'].includes(parts[i].toLowerCase())) {
            company += ' ' + parts[i];
        } else {
            break;
        }
    }

    // The rest is job title
    const job_title = parts.slice(i+1).join(' ').replace(/-/g, ' ');

    return {
        json: {
            company: company.charAt(0).toUpperCase() + company.slice(1),
            job_title: job_title.charAt(0).toUpperCase() + job_title.slice(1),
          link: url
        }
    };
});

const baseURLMakeIt = $('main_config1').first().json.sites[1].baseUrl;
let mergedResults = [];

// Loop through all incoming items (from both previous nodes)
items.forEach(item => {
  const data = item.json;

  // Source 1: Has company and job_title fields
  if (data.company || data.job_title) {
    mergedResults.push({
      json: {
        company: data.company || '',
        job_title: data.job_title || '',
        link: data.link || ''
      }
    });
  }

  // Source 2: Only title and link fields
  else if (data.title || data.jobTitle || data.jobLink) {
    let titles = data.title || data.jobTitle;
    if (!Array.isArray(titles)) titles = [titles];

    let links = data.link || data.jobLink;
    if (!Array.isArray(links)) links = [links];

    links.forEach((link, idx) => {
      const absoluteLink = (link || '').startsWith('http') ? link : baseURLMakeIt + link;
      const job_title = (titles[idx] || titles[0] || '').trim();

      if (absoluteLink) {
        mergedResults.push({
          json: {
            company: '', // No company info in this source
            job_title,
            link: absoluteLink
          }
        });
      }
    });
  }
});

// Deduplicate by link
const uniqueResults = [...new Map(mergedResults.map(r => [r.json.link, r.json])).values()];

// Return all merged items
return uniqueResults.map(r => ({ json: r }));
